    <title>Tokoh Sepak Bola - Web Sepak Bola STMIK Time</title>
    <style>
        body {
            font-family: 'Roboto', Arial, Helvetica, sans-serif;
            margin: 0;
            background-color: #f8f8f8;
            color: #333;
        }

        /* Header */
        header {
            background-color: #008000;
            color: white;
            padding: 20px;
            text-align: center;
            font-size: 30px;
            font-weight: bold;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
        }

        /* Kartu Produk */
        .product-container {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
            gap: 20px;
            padding: 20px;
            margin-top: 30px;
        }

        .product-card {
            background-color: white;
            border-radius: 10px;
            overflow: hidden;
            box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
            transition: transform 0.3s ease;
        }

        .product-card:hover {
            transform: translateY(-10px);
        }

        .product-card img {
            width: 100%;
            height: 200px;
            object-fit: cover;
            border-bottom: 2px solid #f1f1f1;
        }

        .product-card h3 {
            font-size: 18px;
            color: #333;
            margin: 15px;
        }

        .product-card p {
            font-size: 14px;
            color: #777;
            margin: 0 15px;
            line-height: 1.5;
        }

        .product-card .price {
            font-size: 18px;
            font-weight: bold;
            color: #ff6347;
            margin: 15px;
        }

        .btn-buy {
            background-color: #ff6347;
            color: white;
            padding: 12px 0;
            border: none;
            text-decoration: none;
            font-size: 16px;
            display: block;
            text-align: center;
            border-radius: 0 0 10px 10px;
            transition: background-color 0.3s ease;
        }

        .btn-buy:hover {
            background-color: #ffcc00;
        }

        /* Teks dan Tombol */
        .btn {
            background-color: #008000;
            color: white;
            padding: 12px 20px;
            border-radius: 5px;
            text-decoration: none;
            font-size: 16px;
            display: inline-block;
            margin-top: 20px;
            text-align: center;
        }

        .btn:hover {
            background-color: #006400;
        }

        /* Footer */
        footer {
            background-color: #333;
            color: white;
            padding: 15px;
            text-align: center;
            font-size: 14px;
            margin-top: 30px;
        }

        footer a {
            color: #ffcc00;
            text-decoration: none;
        }

        footer a:hover {
            text-decoration: underline;
        }

        /* Link ke Situs Tokoh */
        .link-container {
            text-align: center;
            margin-top: 30px;
        }

        .link-container a {
            color: #008000;
            font-size: 18px;
            text-decoration: none;
        }

        .link-container a:hover {
            text-decoration: underline;
        }
    </style>
</head>
